import './App.css';
import HomePage from './pages/HomePage';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import AboutPage from './pages/AboutPage';
import ArticlesList from './pages/ArticlesList';
import ArticlesPage from './pages/ArticlesPage';
import NavBar from './pages/NavBar';
import NotFound from './pages/NotFound';

function App() {
  return (
    <div className="App">
      <Router>
        <NavBar />
        <div>
          <Switch>
            <Route path="/" component={HomePage} exact></Route>
            <Route path="/about" component={AboutPage} exact></Route>
            {/* check what will happen if we dont give exact */}
            <Route path="/articles-list" component={ArticlesList} exact></Route>
            <Route
              path="/articles/:name"
              component={ArticlesPage}
              exact
            ></Route>
            {/* If we dont use switch then route will try to match the url with all the paths and which all are satidfie
          are rendered.

          By using 

          */}
            <Route component={NotFound}></Route>
          </Switch>
        </div>
      </Router>
    </div>
  );
}

export default App;
